import React, { useEffect } from 'react'
import axios from 'axios';



const AppContext = React.createContext();


let AppProvider = ({children}) => {
     const mymovies= async()=>{
        await axios.get(`http://www.omdbapi.com/?i=tt3896198&apikey=9500443e`)
        .then(request=>{console.log(request.data.result)})
        .catch(error=>{console.log(error)})
        
    };

    
    
    
    return <AppContext.Provider value="zain">{children}</AppContext.Provider>
  
}






export {AppContext,AppProvider};
