import React, { useEffect, useState } from 'react'



const AppContext = React.createContext();


let AppProvider = ({children}) => {
    const [movie , setmovie]=useState([])
    const [error1 , seterror1]=useState[{show : false , error : ""}]
    const url = `http://www.omdbapi.com/?i=tt3896198&apikey=9500443e&s=titanic`;
     const getMovies = async(myUrl)=>{
        try {
            const res = await fetch(myUrl);
            const data = await res.json()
            console.log(data)
            if (data.Response==='true') {
                setmovie(data.Search)
            }
            else{
                seterror1({
                    show : true,
                    error : data.error
                })
    
            }
        } catch (error) {
            console.log(error)
        }

     }
     useEffect(()=>{
        getMovies(url)
     },[])
    
    
    
    return <AppContext.Provider value="zain">{children}</AppContext.Provider>
  
}






export {AppContext,AppProvider};
