import React, { useEffect } from 'react'
import axios from 'axios';



const AppContext = React.createContext();


let AppProvider = ({children}) => {
     const mymovies= async()=>{
        const requuest = await axios.get(`http://www.omdbapi.com/?i=tt3896198&apikey=9500443e`)
        console.log(requuest.data.result)
    }
    
    return <AppContext.Provider value="zain">{children}</AppContext.Provider>
  
}
useEffect(()=>{
    return mymovies();
},[])

export {AppContext,AppProvider};
